import {
  Component,
  EventEmitter,
  Input,
  OnInit,
  Output,
  ViewChild,
} from '@angular/core';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { ActionSheetController, IonModal } from '@ionic/angular';
import { TranslateService } from '@ngx-translate/core';
import { ImageItem } from 'ng-gallery';

@Component({
  selector: 'app-gallery',
  templateUrl: './gallery.component.html',
  styleUrls: ['./gallery.component.scss'],
})
export class GalleryComponent implements OnInit {
  @ViewChild(IonModal) modal!: IonModal;
  @Input() value: any;
  @Input() images: any;
  @Input() gallery: any[] = [];
  @Output() changeEmit = new EventEmitter();

  public isGalleryOpen = false;
  files: any[] = [];

  constructor(
    private actionSheetCtrl: ActionSheetController,
    private _translate: TranslateService
  ) {}

  ngOnInit() {
    for (let i = 0; i < this.value.length; i++) {
      const gallery = this.value[i].gallery
        ? this.value[i].gallery.split(';')
        : [];
      for (let j = 0; j < gallery.length; j++) {
        this.gallery.push('./assets/file-storage/' + gallery[i]);
      }
    }

    // this.images = [
    //   new ImageItem({
    //     src: 'data:image/jpeg;base64,',
    //     thumb:
    //       'data:image/jpeg;base64,',
    //   }),
    //   new ImageItem({
    //     src: 'data:image/jpeg;base64,',
    //     thumb:
    //       'data:image/jpeg;base64,',
    //   }),
    //   new ImageItem({
    //     src: 'data:image/jpeg;base64,',
    //     thumb:
    //       'data:image/jpeg;base64,',
    //   }),
    //   new ImageItem({
    //     src: 'data:image/jpeg;base64,',
    //     thumb:
    //       'data:image/jpeg;base64,',
    //   }),
    // ];
  }

  close() {
    this.modal.isOpen = !this.modal.isOpen;
  }

  canDismiss = async () => {
    const actionSheet = await this.actionSheetCtrl.create({
      header: this._translate.instant('general.areYouSure'),
      buttons: [
        {
          text: this._translate.instant('general.yes'),
          role: 'confirm',
        },
        {
          text: this._translate.instant('general.no'),
          role: 'cancel',
        },
      ],
    });

    actionSheet.present();

    const { role } = await actionSheet.onWillDismiss();

    if (role === 'confirm') {
      return true;
    } else {
      this.modal.isOpen = true;
      return false;
    }
  };

  addMorePhoto = async () => {
    const image = await Camera.getPhoto({
      quality: 90,
      allowEditing: false,
      resultType: CameraResultType.DataUrl,
      source: CameraSource.Prompt,
    });

    this.gallery.push(image.dataUrl);
  };

  fileBrowseHandler(events: any) {
    this.prepareFilesList(events.files);
  }

  prepareFilesList(files: Array<any>) {
    for (const item of files) {
      item.progress = 0;
      this.files.push(item);
    }
    this.packImagesToGallery();
    this.uploadFiles(0);
  }

  packImagesToGallery() {
    for (let i = 0; i < this.files.length; i++) {
      this.gallery.push(URL.createObjectURL(this.files[i]));
    }
  }

  uploadFiles(index: number) {
    let formData = new FormData();

    console.log(formData);

    for (let i = 0; i < this.files.length; i++) {
      formData.append('files[]', this.files[i], this.files[i].name);
    }

    this.changeEmit.emit(this.files);
  }

  getCountOfOtherPictures() {
    if (this.gallery.length > 2) {
      return '+' + (this.gallery.length - 2);
    }
    return false;
  }
}
